import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTheme } from '../../context/ThemeContext';
import ApiService from '../../service/ApiService';
import { FiSun, FiMoon, FiShoppingCart, FiUser, FiLogOut, FiSearch } from 'react-icons/fi';

const Header = () => {
    const navigate = useNavigate();
    const { darkMode, toggleDarkMode } = useTheme();
    const isAuthenticated = ApiService.isAuthenticated();
    const isAdmin = ApiService.isAdmin();
    const userName = ApiService.getUserName();

    const handleLogout = () => {
        ApiService.logout();
        navigate('/login');
    };

    return (
        <header className={`fixed top-0 left-0 right-0 z-50 transition-colors duration-200 ${darkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-800'}`}>
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex items-center justify-between h-16">
                    {/* Logo and Brand */}
                    <div className="flex items-center">
                        <Link to="/" className="flex items-center">
                            <span className={`text-xl font-bold ${darkMode ? 'text-purple-400' : 'text-purple-600'}`}>
                                Quick Commerce
                            </span>
                        </Link>
                    </div>

                    {/* Search Bar */}
                    <div className="flex-1 max-w-2xl mx-8">
                        <div className={`relative rounded-full overflow-hidden border ${darkMode ? 'border-gray-700 bg-gray-800' : 'border-gray-200 bg-gray-50'}`}>
                            <input
                                type="text"
                                placeholder="Search for products..."
                                className={`w-full py-2 pl-4 pr-10 focus:outline-none ${darkMode ? 'bg-gray-800 text-white' : 'bg-gray-50 text-gray-900'}`}
                            />
                            <FiSearch className="absolute right-3 top-2.5 text-gray-400" size={20} />
                        </div>
                    </div>

                    {/* Navigation Items */}
                    <div className="flex items-center space-x-4">
                        {/* Dark Mode Toggle */}
                        <button
                            onClick={toggleDarkMode}
                            className={`p-2 rounded-full hover:bg-opacity-20 ${darkMode ? 'hover:bg-purple-400' : 'hover:bg-purple-600'}`}
                        >
                            {darkMode ? <FiSun size={20} /> : <FiMoon size={20} />}
                        </button>

                        {/* Cart Link */}
                        <Link to="/cart" className="relative p-2 rounded-full hover:bg-opacity-20 hover:bg-purple-600">
                            <FiShoppingCart size={20} />
                            <span className="absolute -top-1 -right-1 bg-purple-600 text-white text-xs rounded-full w-4 h-4 flex items-center justify-center">
                                0
                            </span>
                        </Link>

                        {/* User Menu */}
                        {isAuthenticated ? (
                            <div className="relative group">
                                <button className={`flex items-center space-x-2 p-2 rounded-full hover:bg-opacity-20 ${darkMode ? 'hover:bg-purple-400' : 'hover:bg-purple-600'}`}>
                                    <FiUser size={20} />
                                    <span className="text-sm font-medium">{userName}</span>
                                </button>
                                <div className={`absolute right-0 mt-2 w-48 rounded-md shadow-lg py-1 opacity-0 group-hover:opacity-100 transition-opacity ${darkMode ? 'bg-gray-800' : 'bg-white'} ring-1 ring-black ring-opacity-5`}>
                                    {isAdmin && (
                                        <Link
                                            to="/admin"
                                            className={`block px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                        >
                                            Admin Dashboard
                                        </Link>
                                    )}
                                    <Link
                                        to="/profile"
                                        className={`block px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                    >
                                        Profile
                                    </Link>
                                    <Link
                                        to="/orders"
                                        className={`block px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                    >
                                        Orders
                                    </Link>
                                    <button
                                        onClick={handleLogout}
                                        className={`flex items-center w-full px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                    >
                                        <FiLogOut className="mr-2" size={16} />
                                        Logout
                                    </button>
                                </div>
                            </div>
                        ) : (
                            <Link
                                to="/login"
                                className={`px-4 py-2 rounded-full font-medium ${darkMode ? 'bg-purple-500 hover:bg-purple-400' : 'bg-purple-600 hover:bg-purple-700'} text-white transition-colors`}
                            >
                                Login
                            </Link>
                        )}
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
