import React, { useState, useEffect } from 'react';
import '../../style/homeCarousel.css';

const HomeCarousel = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [imageErrors, setImageErrors] = useState({});

    const fallbackImage = 'https://images.unsplash.com/photo-1472851294608-062f824d29cc?q=80&w=2070'; // Reliable fallback image

    const slides = [
        // {
        //     image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
        //     title: 'Welcome to Quick Commerce',
        //     description: 'Your One-Stop Shopping Destination',
        //     buttonText: 'Shop Now',
        //     buttonLink: '/products'
        // },
        // {
        //     image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
        //     title: 'Fashion Trends 2024',
        //     description: 'Discover the latest styles and trends',
        //     buttonText: 'Shop Fashion',
        //     buttonLink: '/products?category=fashion'
        // },
        // {
        //     image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
        //     title: 'Electronics & Gadgets',
        //     description: 'Explore cutting-edge technology',
        //     buttonText: 'View Electronics',
        //     buttonLink: '/products?category=electronics'
        // },
        // {
        //     image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
        //     title: 'Home & Living',
        //     description: 'Transform your living space',
        //     buttonText: 'Shop Home',
        //     buttonLink: '/products?category=home'
        // },
        // {
        //     image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
        //     title: 'Special Offers',
        //     description: 'Get up to 50% off on selected items',
        //     buttonText: 'View Deals',
        //     buttonLink: '/products'
        // },
        {
            image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
            title: 'Books & Media',
            description: 'Dive into a world of stories and knowledge',
            buttonText: 'Explore Books',
            buttonLink: '/products?category=books'
        },
        // {
        //     image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
        //     title: 'Books & Media',
        //     description: 'Dive into a world of stories and knowledge',
        //     buttonText: 'Explore Books',
        //     buttonLink: '/products?category=books'
        // },
        // {
        //     image: 'https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?q=80&w=2070',
        //     title: 'Books & Media',
        //     description: 'Dive into a world of stories and knowledge',
        //     buttonText: 'Explore Books',
        //     buttonLink: '/products?category=books'
        // }
    ];

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentSlide((prevSlide) => 
                prevSlide === slides.length - 1 ? 0 : prevSlide + 1
            );
        }, 5000);

        return () => clearInterval(timer);
    }, [slides.length]);

    const goToSlide = (index) => {
        setCurrentSlide(index);
    };

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => 
            prevSlide === slides.length - 1 ? 0 : prevSlide + 1
        );
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => 
            prevSlide === 0 ? slides.length - 1 : prevSlide - 1
        );
    };

    const handleImageError = (index) => {
        setImageErrors(prev => ({
            ...prev,
            [index]: true
        }));
    };

    return (
        <div className="carousel-container">
            <div className="carousel">
                {slides.map((slide, index) => (
                    <div
                        key={index}
                        className={`carousel-slide ${index === currentSlide ? 'active' : ''}`}
                        style={{ transform: `translateX(-${currentSlide * 100}%)` }}
                    >
                        <div className="carousel-image">
                            <img 
                                src={imageErrors[index] ? fallbackImage : slide.image} 
                                alt={slide.title}
                                onError={() => handleImageError(index)}
                            />
                        </div>
                        <div className="carousel-content">
                            <h2>{slide.title}</h2>
                            <p>{slide.description}</p>
                            <a href={slide.buttonLink} className="carousel-button">
                                {slide.buttonText}
                            </a>
                        </div>
                    </div>
                ))}
            </div>

            <button className="carousel-control prev" onClick={prevSlide}>
                ‹
            </button>
            <button className="carousel-control next" onClick={nextSlide}>
                ›
            </button>

            <div className="carousel-indicators">
                {slides.map((_, index) => (
                    <button
                        key={index}
                        className={`carousel-indicator ${index === currentSlide ? 'active' : ''}`}
                        onClick={() => goToSlide(index)}
                    />
                ))}
            </div>
        </div>
    );
};

export default HomeCarousel;
