import React from "react";
import { Link } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useTheme } from "../../context/ThemeContext";
import { FiMinus, FiPlus, FiShoppingCart } from "react-icons/fi";

const ProductList = ({products}) => {
    const {cart, dispatch} = useCart();
    const { darkMode } = useTheme();

    const addToCart = (product) => {
        dispatch({type: 'ADD_ITEM', payload: product});
    }

    const incrementItem = (product) => {
        dispatch({type: 'INCREMENT_ITEM', payload: product});
    }

    const decrementItem = (product) => {
        const cartItem = cart.find(item => item.id === product.id);
        if (cartItem && cartItem.quantity > 1) {
            dispatch({type: 'DECREMENT_ITEM', payload: product}); 
        } else {
            dispatch({type: 'REMOVE_ITEM', payload: product}); 
        }
    }

    return (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 py-8">
            {products.map((product, index) => {
                const cartItem = cart.find(item => item.id === product.id);
                return (
                    <div 
                        key={index}
                        className={`group relative rounded-lg overflow-hidden shadow-md hover:shadow-xl transition-shadow duration-300 ${
                            darkMode ? 'bg-gray-800' : 'bg-white'
                        }`}
                    >
                        <Link to={`/product/${product.id}`} className="block aspect-w-1 aspect-h-1">
                            <img 
                                src={product.imageUrl} 
                                alt={product.name} 
                                className="w-full h-48 object-cover object-center group-hover:opacity-75 transition-opacity"
                            />
                        </Link>
                        <div className="p-4">
                            <Link to={`/product/${product.id}`}>
                                <h3 className={`text-lg font-medium mb-2 ${
                                    darkMode ? 'text-white' : 'text-gray-900'
                                } hover:text-purple-600 transition-colors`}>
                                    {product.name}
                                </h3>
                                <p className={`text-sm mb-2 line-clamp-2 ${
                                    darkMode ? 'text-gray-300' : 'text-gray-500'
                                }`}>
                                    {product.description}
                                </p>
                                <span className="text-xl font-bold text-purple-600">
                                    ${product.price.toFixed(2)}
                                </span>
                            </Link>
                            <div className="mt-4">
                                {cartItem ? (
                                    <div className="flex items-center justify-between">
                                        <button 
                                            onClick={(e) => {
                                                e.preventDefault();
                                                decrementItem(product);
                                            }}
                                            className={`p-2 rounded-lg ${
                                                darkMode 
                                                    ? 'bg-gray-700 hover:bg-gray-600' 
                                                    : 'bg-gray-100 hover:bg-gray-200'
                                            } transition-colors`}
                                        >
                                            <FiMinus size={16} />
                                        </button>
                                        <span className={`text-lg font-medium ${
                                            darkMode ? 'text-white' : 'text-gray-900'
                                        }`}>
                                            {cartItem.quantity}
                                        </span>
                                        <button 
                                            onClick={(e) => {
                                                e.preventDefault();
                                                incrementItem(product);
                                            }}
                                            className={`p-2 rounded-lg ${
                                                darkMode 
                                                    ? 'bg-gray-700 hover:bg-gray-600' 
                                                    : 'bg-gray-100 hover:bg-gray-200'
                                            } transition-colors`}
                                        >
                                            <FiPlus size={16} />
                                        </button>
                                    </div>
                                ) : (
                                    <button 
                                        onClick={(e) => {
                                            e.preventDefault();
                                            addToCart(product);
                                        }}
                                        className="w-full flex items-center justify-center space-x-2 py-2 px-4 rounded-lg bg-purple-600 hover:bg-purple-700 text-white font-medium transition-colors"
                                    >
                                        <FiShoppingCart size={18} />
                                        <span>Add to Cart</span>
                                    </button>
                                )}
                            </div>
                        </div>
                    </div>
                );
            })}
        </div>
    );
};

export default ProductList;
