import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ApiService from "../../service/ApiService";
import '../../style/profile.css';
import Pagination from "../common/Pagination";

const ProfilePage = () => {
    const [userInfo, setUserInfo] = useState(null);
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;
    const navigate = useNavigate();

    useEffect(() => {
        fetchUserInfo();
        fetchOrders();
    }, []);

    const fetchUserInfo = async () => {
        try {
            setLoading(true);
            setError(null);
            const response = await ApiService.getLoggedInUserInfo();
            setUserInfo(response);
        } catch (error) {
            console.error('Error fetching user info:', error);
            setError(error.message || 'Unable to fetch user info');
        }
    }

    const fetchOrders = async () => {
        try {
            const userId = localStorage.getItem('userId');
            if (!userId) {
                throw new Error('User ID not found');
            }
            const response = await ApiService.getOrdersByUserId(parseInt(userId));
            setOrders(response || []);
        } catch (error) {
            console.error('Error fetching orders:', error);
            setError(error.message || 'Unable to fetch orders');
        } finally {
            setLoading(false);
        }
    }

    const handleLogout = () => {
        ApiService.logout();
        navigate('/login');
    };

    if (loading) {
        return <div className="profile-page">Loading...</div>;
    }

    if (error) {
        return <div className="profile-page error-message">{error}</div>;
    }

    if (!userInfo) {
        return <div className="profile-page">No user information available</div>;
    }

    const totalPages = Math.ceil(orders.length / itemsPerPage);

    const paginatedOrders = orders.slice(
        (currentPage - 1) * itemsPerPage,
        currentPage * itemsPerPage
    );

    return (
        <div className="profile-page">
            <div className="profile-content">
                <h2>Welcome {userInfo.name}</h2>

                <div className="user-info">
                    <p><strong>Name: </strong>{userInfo.name}</p>
                    <p><strong>Email: </strong>{userInfo.email}</p>
                    <p><strong>Role: </strong>{userInfo.role}</p>
                </div>

                <div className="orders-section">
                    <h3>Order History</h3>
                    {orders.length > 0 ? (
                        <>
                            <ul className="orders-list">
                                {paginatedOrders.map(order => (
                                    <li key={order.id} className="order-item">
                                        <div className="order-header">
                                            <h4>Order #{order.id}</h4>
                                            <span className={`order-status ${order.status.toLowerCase()}`}>
                                                {order.status}
                                            </span>
                                        </div>
                                        <div className="order-details">
                                            <p><strong>Date: </strong>{new Date(order.orderDate).toLocaleDateString()}</p>
                                            <p><strong>Total Amount: </strong>${order.totalAmount.toFixed(2)}</p>
                                            <p><strong>Shipping Address: </strong>{order.shippingAddress}</p>
                                        </div>
                                        <div className="order-items">
                                            {order.orderItems.map(item => (
                                                <div key={item.id} className="order-product">
                                                    <img 
                                                        src={item.product?.imageUrl || 'https://img.freepik.com/free-vector/illustration-gallery-icon_53876-27002.jpg'} 
                                                        alt={item.product?.name || 'Product Image'} 
                                                    />
                                                    <div>
                                                        <p><strong>Product: </strong>{item.product?.name || 'Unknown Product'}</p>
                                                        <p><strong>Quantity: </strong>{item.quantity}</p>
                                                        <p><strong>Price: </strong>${item.price.toFixed(2)}</p>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <Pagination
                                currentPage={currentPage}
                                totalPages={totalPages}
                                onPageChange={(page) => setCurrentPage(page)}
                            />
                        </>
                    ) : (
                        <p>No orders found</p>
                    )}
                </div>
            </div>

            <div className="logout-section">
                <button onClick={handleLogout} className="logout-button">
                    Logout
                </button>
            </div>
        </div>
    );
}

export default ProfilePage;
