import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import ApiService from "../../service/ApiService";
import '../../style/checkout.css';

const CheckoutPage = () => {
    const { cart, dispatch } = useCart();
    const navigate = useNavigate();
    const [step, setStep] = useState(1); // 1: Address, 2: Payment
    const [message, setMessage] = useState(null);
    const [address, setAddress] = useState({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: ''
    });

    useEffect(() => {
        if (!ApiService.isAuthenticated()) {
            setMessage("Please login to continue checkout");
            setTimeout(() => {
                navigate("/login");
            }, 2000);
            return;
        }
        
        // Load user's saved address if available
        fetchUserAddress();
    }, []);

    const fetchUserAddress = async () => {
        try {
            const response = await ApiService.getLoggedInUserInfo();
            if (response.address) {
                setAddress(response.address);
            }
        } catch (error) {
            console.error("Error fetching address:", error);
        }
    };

    const handleAddressChange = (e) => {
        const { name, value } = e.target;
        setAddress(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleAddressSubmit = (e) => {
        e.preventDefault();
        // Validate address fields
        if (!address.street || !address.city || !address.state || !address.zipCode || !address.country) {
            setMessage("Please fill in all address fields");
            return;
        }
        setStep(2); // Move to payment step
    };

    const handlePlaceOrder = async () => {
        const orderItems = cart.map(item => ({
            productId: item.id,
            quantity: item.quantity
        }));

        const shippingAddress = `${address.street}, ${address.city}, ${address.state} ${address.zipCode}, ${address.country}`;

        const orderRequest = {
            userId: parseInt(localStorage.getItem('userId')),
            shippingAddress: shippingAddress,
            orderItems: orderItems
        };

        try {
            const response = await ApiService.createOrder(orderRequest);
            setMessage("Order placed successfully!");
            dispatch({ type: 'CLEAR_CART' });

            setTimeout(() => {
                navigate('/orders');
            }, 2000);

        } catch (error) {
            setMessage(error.response?.data?.message || error.message || 'Failed to place order');
        }
    };

    if (cart.length === 0) {
        return (
            <div className="checkout-page">
                <h2>Your cart is empty</h2>
                <button onClick={() => navigate('/')}>Continue Shopping</button>
            </div>
        );
    }

    const totalAmount = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    return (
        <div className="checkout-page">
            <h2>Checkout</h2>
            {message && <div className="message">{message}</div>}

            <div className="checkout-steps">
                <div className={`step ${step === 1 ? 'active' : ''}`}>1. Shipping Address</div>
                <div className={`step ${step === 2 ? 'active' : ''}`}>2. Payment</div>
            </div>

            {step === 1 ? (
                <div className="address-form">
                    <h3>Shipping Address</h3>
                    <form onSubmit={handleAddressSubmit}>
                        <div className="form-group">
                            <label>Street:</label>
                            <input
                                type="text"
                                name="street"
                                value={address.street}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>City:</label>
                            <input
                                type="text"
                                name="city"
                                value={address.city}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>State:</label>
                            <input
                                type="text"
                                name="state"
                                value={address.state}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Zip Code:</label>
                            <input
                                type="text"
                                name="zipCode"
                                value={address.zipCode}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Country:</label>
                            <input
                                type="text"
                                name="country"
                                value={address.country}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                        <button type="submit">Continue to Payment</button>
                    </form>
                </div>
            ) : (
                <div className="payment-section">
                    <h3>Order Summary</h3>
                    <div className="order-summary">
                        {cart.map(item => (
                            <div key={item.id} className="order-item">
                                <span>{item.name} x {item.quantity}</span>
                                <span>${(item.price * item.quantity).toFixed(2)}</span>
                            </div>
                        ))}
                        <div className="total">
                            <strong>Total:</strong>
                            <strong>${totalAmount.toFixed(2)}</strong>
                        </div>
                    </div>
                    
                    <div className="shipping-address">
                        <h4>Shipping to:</h4>
                        <p>{address.street}</p>
                        <p>{address.city}, {address.state} {address.zipCode}</p>
                        <p>{address.country}</p>
                    </div>

                    <div className="payment-options">
                        <h4>Payment Method</h4>
                        <div className="payment-method">
                            <input
                                type="radio"
                                id="credit-card"
                                name="payment"
                                value="credit-card"
                                checked
                                readOnly
                            />
                            <label htmlFor="credit-card">Credit Card (Default)</label>
                        </div>
                    </div>

                    <div className="actions">
                        <button onClick={() => setStep(1)}>Back to Address</button>
                        <button onClick={handlePlaceOrder}>Place Order</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CheckoutPage;
