import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ApiService from "../../service/ApiService";
import '../../style/address.css';

const AddressPage = () => {
    const [address, setAddress] = useState({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: ''
    });

    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        if (location.pathname === '/edit-address') {
            fetchUserInfo();
        }
    }, [location.pathname]);

    const fetchUserInfo = async () => {
        try {
            setLoading(true);
            setError(null);
            const response = await ApiService.getLoggedInUserInfo();
            if (response.address) {
                setAddress({
                    street: response.address.street || '',
                    city: response.address.city || '',
                    state: response.address.state || '',
                    zipCode: response.address.zipCode || '',
                    country: response.address.country || ''
                });
            }
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Unable to fetch user information");
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            setError(null);
            const userId = parseInt(localStorage.getItem('userId'));
            if (!userId) {
                throw new Error('User ID not found. Please login again.');
            }
            const addressData = {
                ...address,
                userId: userId
            };
            await ApiService.saveAddress(addressData);
            navigate("/profile");
        } catch (error) {
            setError(error.response?.data?.message || error.message || "Failed to save/update address");
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <div className="address-page">Loading...</div>;
    }

    return (
        <div className="address-page">
            <h2>{location.pathname === '/edit-address' ? 'Edit Address' : "Add Address"}</h2>
            {error && <p className="error-message">{error}</p>}

            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Street:</label>
                    <input
                        type="text"
                        name="street"
                        value={address.street}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>City:</label>
                    <input
                        type="text"
                        name="city"
                        value={address.city}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>State:</label>
                    <input
                        type="text"
                        name="state"
                        value={address.state}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Zip Code:</label>
                    <input
                        type="text"
                        name="zipCode"
                        value={address.zipCode}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Country:</label>
                    <input
                        type="text"
                        name="country"
                        value={address.country}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="button-group">
                    <button type="submit" disabled={loading}>
                        {loading ? 'Saving...' : (location.pathname === '/edit-address' ? 'Update Address' : 'Save Address')}
                    </button>
                    <button type="button" onClick={() => navigate('/profile')}>
                        Cancel
                    </button>
                </div>
            </form>
        </div>
    );
};

export default AddressPage;
