import axios from "axios";

export default class ApiService {

    static BASE_URL = "http://localhost:8080";

    static getHeader() {
        const token = localStorage.getItem("token");
        return {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json"
        };
    }

    /**AUTH && USERS API */
    static async registerUser(registration) {
        try {
            const response = await axios.post(`${this.BASE_URL}/auth-service/api/register`, registration);
            if (response.data.success) {
                const token = response.headers.authorization?.split(" ")[1];
                if (token) {
                    localStorage.setItem("token", token);
                    localStorage.setItem("user", JSON.stringify(response.data.data));
                    return response.data;
                }
                throw new Error("No token received from server");
            }
            throw new Error(response.data.message || "Registration failed");
        } catch (error) {
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    }

    static async loginUser(loginDetails) {
        try {
            const response = await axios.post(`${this.BASE_URL}/auth-service/api/login`, loginDetails);
            if (response.data.success) {
                const token = response.headers.authorization?.split(" ")[1];
                if (token) {
                    localStorage.setItem("token", token);
                    localStorage.setItem("user", JSON.stringify(response.data.data));
                    return response.data;
                }
                throw new Error("No token received from server");
            }
            throw new Error(response.data.message || "Login failed");
        } catch (error) {
            if (error.response?.data?.message) {
                throw new Error(error.response.data.message);
            }
            throw error;
        }
    }

    static async getLoggedInUserInfo() {
        const response = await axios.get(`${this.BASE_URL}/user-service/api/user/my-info`, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static logout() {
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        localStorage.removeItem("role");
    }

    static isAuthenticated() {
        return !!localStorage.getItem("token");
    }

    static isAdmin() {
        const user = JSON.parse(localStorage.getItem("user") || "{}");
        return user.role === "ADMIN";
    }

    static getUserName() {
        const user = JSON.parse(localStorage.getItem("user") || "{}");
        return user.name || "";
    }

    /**PRODUCT ENDPOINT */

    static async addProduct(formData) {
        const response = await axios.post(`${this.BASE_URL}/product-service/api/products`, formData, {
            headers: {
                ...this.getHeader(),
                "Content-Type": "multipart/form-data"
            }
        });
        return response.data;
    }

    static async updateProduct(formData) {
        const response = await axios.put(`${this.BASE_URL}/product-service/api/products`, formData, {
            headers: {
                ...this.getHeader(),
                "Content-Type": "multipart/form-data"
            }
        });
        return response.data;
    }

    static async getAllProducts() {
        const response = await axios.get(`${this.BASE_URL}/product-service/api/products`);
        return response.data;
    }

    static async searchProducts(searchValue) {
        const response = await axios.get(`${this.BASE_URL}/product-service/api/products/search`, {
            params: { searchValue }
        });
        return response.data;
    }

    static async getAllProductsByCategoryId(categoryId) {
        const response = await axios.get(`${this.BASE_URL}/product-service/api/products/category/${categoryId}`);
        return response.data;
    }

    static async getProductById(productId) {
        const response = await axios.get(`${this.BASE_URL}/product-service/api/products/${productId}`);
        return response.data;
    }

    static async deleteProduct(productId) {
        const response = await axios.delete(`${this.BASE_URL}/product-service/api/products/${productId}`, {
            headers: this.getHeader()
        });
        return response.data;
    }

    /**CATEGORY */
    static async createCategory(body) {
        const response = await axios.post(`${this.BASE_URL}/product-service/api/categories`, body, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async getAllCategory() {
        const response = await axios.get(`${this.BASE_URL}/product-service/api/categories`);
        return response.data;
    }

    static async getCategoryById(categoryId) {
        const response = await axios.get(`${this.BASE_URL}/product-service/api/categories/${categoryId}`);
        return response.data;
    }

    static async updateCategory(categoryId, body) {
        const response = await axios.put(`${this.BASE_URL}/product-service/api/categories/${categoryId}`, body, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async deleteCategory(categoryId) {
        const response = await axios.delete(`${this.BASE_URL}/product-service/api/categories/${categoryId}`, {
            headers: this.getHeader()
        });
        return response.data;
    }

    /**ORDER */
    static async createOrder(body) {
        const response = await axios.post(`${this.BASE_URL}/order-service/api/orders`, body, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async getAllOrders() {
        const response = await axios.get(`${this.BASE_URL}/order-service/api/orders`, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async getOrderItemById(itemId) {
        const response = await axios.get(`${this.BASE_URL}/order-service/api/orders/items/${itemId}`, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async getAllOrderItemsByStatus(status) {
        const response = await axios.get(`${this.BASE_URL}/order-service/api/orders/status/${status}`, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async updateOrderitemStatus(orderItemId, status) {
        const response = await axios.put(`${this.BASE_URL}/order-service/api/orders/items/${orderItemId}/status`, {}, {
            headers: this.getHeader(),
            params: { status }
        });
        return response.data;
    }

    /**ADDRESS */
    static async saveAddress(body) {
        const response = await axios.post(`${this.BASE_URL}/user-service/api/addresses`, body, {
            headers: this.getHeader()
        });
        return response.data;
    }

    /**INVENTORY */
    static async updateInventory(productId, quantity) {
        const response = await axios.put(`${this.BASE_URL}/inventory-service/api/inventory/${productId}`, {
            quantity
        }, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async getInventory(productId) {
        const response = await axios.get(`${this.BASE_URL}/inventory-service/api/inventory/${productId}`);
        return response.data;
    }

    /**PAYMENT */
    static async processPayment(orderId) {
        const response = await axios.post(`${this.BASE_URL}/payment-service/api/payments`, {
            orderId
        }, {
            headers: this.getHeader()
        });
        return response.data;
    }

    static async getPaymentStatus(paymentId) {
        const response = await axios.get(`${this.BASE_URL}/payment-service/api/payments/${paymentId}`, {
            headers: this.getHeader()
        });
        return response.data;
    }
}
