import React, { useState, useEffect } from 'react';
import '../../style/homeCarousel.css';

const HomeCarousel = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const slides = [
        {
            image: 'https://images.unsplash.com/photo-1607082348824-0a96f2a4b9da?q=80&w=2070&auto=format&fit=crop',
            title: 'Welcome to Quick Commerce',
            description: 'Your One-Stop Shopping Destination',
            buttonText: 'Shop Now',
            buttonLink: '/products'
        },
        {
            image: 'https://images.unsplash.com/photo-1607082349566-187342175e2f?q=80&w=2070&auto=format&fit=crop',
            title: 'New Arrivals',
            description: 'Discover our latest collection',
            buttonText: 'Explore',
            buttonLink: '/products'
        },
        {
            image: 'https://images.unsplash.com/photo-1607082350899-7e105aa886ae?q=80&w=2070&auto=format&fit=crop',
            title: 'Special Offers',
            description: 'Get up to 50% off on selected items',
            buttonText: 'View Deals',
            buttonLink: '/products'
        }
    ];

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentSlide((prevSlide) => 
                prevSlide === slides.length - 1 ? 0 : prevSlide + 1
            );
        }, 5000);

        return () => clearInterval(timer);
    }, [slides.length]);

    const goToSlide = (index) => {
        setCurrentSlide(index);
    };

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => 
            prevSlide === slides.length - 1 ? 0 : prevSlide + 1
        );
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => 
            prevSlide === 0 ? slides.length - 1 : prevSlide - 1
        );
    };

    return (
        <div className="carousel-container">
            <div className="carousel">
                {slides.map((slide, index) => (
                    <div
                        key={index}
                        className={`carousel-slide ${index === currentSlide ? 'active' : ''}`}
                        style={{ transform: `translateX(-${currentSlide * 100}%)` }}
                    >
                        <div className="carousel-image">
                            <img src={slide.image} alt={slide.title} />
                        </div>
                        <div className="carousel-content">
                            <h2>{slide.title}</h2>
                            <p>{slide.description}</p>
                            <a href={slide.buttonLink} className="carousel-button">
                                {slide.buttonText}
                            </a>
                        </div>
                    </div>
                ))}
            </div>

            <button className="carousel-control prev" onClick={prevSlide}>
                ‹
            </button>
            <button className="carousel-control next" onClick={nextSlide}>
                ›
            </button>

            <div className="carousel-indicators">
                {slides.map((_, index) => (
                    <button
                        key={index}
                        className={`carousel-indicator ${index === currentSlide ? 'active' : ''}`}
                        onClick={() => goToSlide(index)}
                    />
                ))}
            </div>
        </div>
    );
};

export default HomeCarousel;
