import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTheme } from '../../context/ThemeContext';
import ApiService from '../../service/ApiService';
import { FiSun, FiMoon, FiShoppingCart, FiUser, FiLogOut, FiSearch, FiMenu } from 'react-icons/fi';

const Header = () => {
    const navigate = useNavigate();
    const { darkMode, toggleDarkMode } = useTheme();
    const isAuthenticated = ApiService.isAuthenticated();
    const isAdmin = ApiService.isAdmin();
    const userName = ApiService.getUserName();
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');

    const handleLogout = () => {
        ApiService.logout();
        navigate('/login');
    };

    const handleSearch = (e) => {
        e.preventDefault();
        // Implement search functionality
        if (searchQuery.trim()) {
            navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
        }
    };

    return (
        <header className={`fixed top-0 left-0 right-0 z-50 shadow-md transition-colors duration-200 ${darkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-800'}`}>
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex items-center justify-between h-16">
                    {/* Logo and Brand */}
                    <div className="flex items-center">
                        <Link to="/" className="flex items-center space-x-2">
                            <span className={`text-2xl font-bold ${darkMode ? 'text-purple-400' : 'text-purple-600'} hover:opacity-90 transition-opacity`}>
                                Quick Commerce
                            </span>
                        </Link>
                    </div>

                    {/* Search Bar */}
                    <form onSubmit={handleSearch} className="hidden md:flex flex-1 max-w-2xl mx-8">
                        <div className={`relative flex-1 rounded-lg overflow-hidden border ${darkMode ? 'border-gray-700 bg-gray-800' : 'border-gray-200 bg-gray-50'}`}>
                            <input
                                type="text"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                placeholder="Search for products..."
                                className={`w-full py-2 pl-4 pr-10 focus:outline-none focus:ring-2 focus:ring-purple-500 ${darkMode ? 'bg-gray-800 text-white placeholder-gray-400' : 'bg-gray-50 text-gray-900 placeholder-gray-500'}`}
                            />
                            <button type="submit" className="absolute right-2 top-2 text-gray-400 hover:text-gray-600">
                                <FiSearch size={20} />
                            </button>
                        </div>
                    </form>

                    {/* Navigation Items */}
                    <div className="flex items-center space-x-4">
                        {/* Dark Mode Toggle */}
                        <button
                            onClick={toggleDarkMode}
                            className={`p-2 rounded-full hover:bg-opacity-20 transition-colors ${darkMode ? 'hover:bg-purple-400' : 'hover:bg-purple-600'}`}
                            aria-label="Toggle dark mode"
                        >
                            {darkMode ? <FiSun size={20} /> : <FiMoon size={20} />}
                        </button>

                        {/* Cart Link */}
                        <Link 
                            to="/cart" 
                            className={`relative p-2 rounded-full hover:bg-opacity-20 transition-colors ${darkMode ? 'hover:bg-purple-400' : 'hover:bg-purple-600'}`}
                            aria-label="Shopping cart"
                        >
                            <FiShoppingCart size={20} />
                            <span className="absolute -top-1 -right-1 bg-purple-600 text-white text-xs font-bold rounded-full w-5 h-5 flex items-center justify-center">
                                0
                            </span>
                        </Link>

                        {/* User Menu */}
                        {isAuthenticated ? (
                            <div className="relative group">
                                <button 
                                    className={`flex items-center space-x-2 p-2 rounded-lg hover:bg-opacity-20 transition-colors ${darkMode ? 'hover:bg-purple-400' : 'hover:bg-purple-600'}`}
                                    onClick={() => setIsMenuOpen(!isMenuOpen)}
                                >
                                    <FiUser size={20} />
                                    <span className="hidden sm:inline text-sm font-medium">{userName}</span>
                                </button>
                                <div className={`absolute right-0 mt-2 w-48 rounded-lg shadow-lg py-1 ${darkMode ? 'bg-gray-800' : 'bg-white'} ring-1 ring-black ring-opacity-5 transform transition-all duration-200 ${isMenuOpen ? 'opacity-100 scale-100' : 'opacity-0 scale-95 pointer-events-none'}`}>
                                    {isAdmin && (
                                        <Link
                                            to="/admin"
                                            className={`block px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                        >
                                            Admin Dashboard
                                        </Link>
                                    )}
                                    <Link
                                        to="/profile"
                                        className={`block px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                    >
                                        Profile
                                    </Link>
                                    <Link
                                        to="/orders"
                                        className={`block px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                    >
                                        Orders
                                    </Link>
                                    <button
                                        onClick={handleLogout}
                                        className={`flex items-center w-full px-4 py-2 text-sm ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
                                    >
                                        <FiLogOut className="mr-2" size={16} />
                                        Logout
                                    </button>
                                </div>
                            </div>
                        ) : (
                            <Link
                                to="/login"
                                className={`px-4 py-2 rounded-lg font-medium transition-colors ${darkMode ? 'bg-purple-500 hover:bg-purple-400 text-white' : 'bg-purple-600 hover:bg-purple-700 text-white'}`}
                            >
                                Login
                            </Link>
                        )}
                    </div>
                </div>
            </div>

            {/* Mobile Search Bar */}
            <div className="md:hidden px-4 pb-4">
                <form onSubmit={handleSearch} className="flex">
                    <div className={`relative flex-1 rounded-lg overflow-hidden border ${darkMode ? 'border-gray-700 bg-gray-800' : 'border-gray-200 bg-gray-50'}`}>
                        <input
                            type="text"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            placeholder="Search for products..."
                            className={`w-full py-2 pl-4 pr-10 focus:outline-none focus:ring-2 focus:ring-purple-500 ${darkMode ? 'bg-gray-800 text-white placeholder-gray-400' : 'bg-gray-50 text-gray-900 placeholder-gray-500'}`}
                        />
                        <button type="submit" className="absolute right-2 top-2 text-gray-400 hover:text-gray-600">
                            <FiSearch size={20} />
                        </button>
                    </div>
                </form>
            </div>
        </header>
    );
};

export default Header;
