import React from "react";
import { useTheme } from "../../context/ThemeContext";
import { FiChevronLeft, FiChevronRight } from "react-icons/fi";

const Pagination = ({ currentPage, totalPages, onPageChange }) => {
    const { darkMode } = useTheme();

    const getPageNumbers = () => {
        const delta = 2;
        const range = [];
        const rangeWithDots = [];

        for (
            let i = Math.max(2, currentPage - delta);
            i <= Math.min(totalPages - 1, currentPage + delta);
            i++
        ) {
            range.push(i);
        }

        if (currentPage - delta > 2) {
            rangeWithDots.push(1, '...');
        } else {
            rangeWithDots.push(1);
        }

        rangeWithDots.push(...range);

        if (currentPage + delta < totalPages - 1) {
            rangeWithDots.push('...', totalPages);
        } else if (totalPages > 1) {
            rangeWithDots.push(totalPages);
        }

        return rangeWithDots;
    };

    if (totalPages <= 1) return null;

    return (
        <nav className="flex items-center justify-center space-x-1">
            <button
                onClick={() => onPageChange(currentPage - 1)}
                disabled={currentPage === 1}
                className={`p-2 rounded-lg flex items-center justify-center ${
                    darkMode
                        ? 'bg-gray-800 text-gray-300 hover:bg-gray-700 disabled:bg-gray-900 disabled:text-gray-600'
                        : 'bg-white text-gray-500 hover:bg-gray-50 disabled:bg-gray-100 disabled:text-gray-400'
                } transition-colors disabled:cursor-not-allowed`}
                aria-label="Previous page"
            >
                <FiChevronLeft size={20} />
            </button>

            {getPageNumbers().map((number, index) => (
                <button
                    key={index}
                    onClick={() => typeof number === 'number' && onPageChange(number)}
                    disabled={typeof number !== 'number'}
                    className={`min-w-[40px] h-10 flex items-center justify-center rounded-lg text-sm font-medium transition-colors ${
                        typeof number !== 'number'
                            ? `${darkMode ? 'text-gray-500' : 'text-gray-400'} cursor-default`
                            : number === currentPage
                            ? `${darkMode ? 'bg-purple-600 text-white' : 'bg-purple-600 text-white'}`
                            : `${
                                darkMode
                                    ? 'bg-gray-800 text-gray-300 hover:bg-gray-700'
                                    : 'bg-white text-gray-700 hover:bg-gray-50'
                              }`
                    }`}
                >
                    {number}
                </button>
            ))}

            <button
                onClick={() => onPageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
                className={`p-2 rounded-lg flex items-center justify-center ${
                    darkMode
                        ? 'bg-gray-800 text-gray-300 hover:bg-gray-700 disabled:bg-gray-900 disabled:text-gray-600'
                        : 'bg-white text-gray-500 hover:bg-gray-50 disabled:bg-gray-100 disabled:text-gray-400'
                } transition-colors disabled:cursor-not-allowed`}
                aria-label="Next page"
            >
                <FiChevronRight size={20} />
            </button>
        </nav>
    );
};

export default Pagination;
