import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import ProductList from "../common/ProductList";
import Pagination from "../common/Pagination";
import ApiService from "../../service/ApiService";
import { useTheme } from "../../context/ThemeContext";
import { FiLoader } from "react-icons/fi";

const Home = () => {
    const location = useLocation();
    const { darkMode } = useTheme();
    const [products, setProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const itemsPerPage = 12;

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                let allProducts = [];
                const queryParams = new URLSearchParams(location.search);
                const searchItem = queryParams.get('search');

                if (searchItem) {
                    const response = await ApiService.searchProducts(searchItem);
                    allProducts = response.productList || [];
                } else {
                    const response = await ApiService.getAllProducts();
                    allProducts = response.productList || [];
                }

                setTotalPages(Math.ceil(allProducts.length / itemsPerPage));
                setProducts(allProducts.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage));
                setError(null);
            } catch (error) {
                setError(error.response?.data?.message || error.message || 'Unable to fetch products');
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, [location.search, currentPage]);

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="flex flex-col items-center space-y-4">
                    <FiLoader className="animate-spin text-purple-600" size={40} />
                    <p className={darkMode ? 'text-white' : 'text-gray-900'}>Loading products...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-center space-y-4">
                    <p className={`text-lg font-medium ${darkMode ? 'text-red-400' : 'text-red-600'}`}>
                        {error}
                    </p>
                    <button 
                        onClick={() => window.location.reload()}
                        className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
                    >
                        Try Again
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className={`min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                {products.length === 0 ? (
                    <div className="flex items-center justify-center min-h-[50vh]">
                        <p className={`text-lg font-medium ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                            No products found
                        </p>
                    </div>
                ) : (
                    <>
                        <ProductList products={products} />
                        <div className="py-8">
                            <Pagination
                                currentPage={currentPage}
                                totalPages={totalPages}
                                onPageChange={(page) => setCurrentPage(page)}
                            />
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Home;
