import React, { useEffect, useState } from 'react';
import HomeCarousel from '../home/HomeCarousel';
import ProductCard from '../common/ProductCard';
import ApiService from '../../service/ApiService';
import '../../style/homePage.css';

const Home = () => {
    const [featuredProducts, setFeaturedProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchFeaturedProducts();
    }, []);

    const fetchFeaturedProducts = async () => {
        try {
            setLoading(true);
            const response = await ApiService.getAllProducts();
            // Get the first 8 products as featured products
            const featured = response.productList.slice(0, 8);
            setFeaturedProducts(featured);
        } catch (error) {
            console.error('Error fetching featured products:', error);
            setError('Failed to load featured products');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="home-page">
            <HomeCarousel />

            <div className="container">
                <section className="featured-section">
                    <h2 className="section-title">Featured Products</h2>
                    <p className="section-description">
                        Discover our handpicked selection of top products
                    </p>

                    {loading && <div className="loading">Loading featured products...</div>}
                    {error && <div className="error">{error}</div>}

                    <div className="featured-products">
                        {featuredProducts.map(product => (
                            <ProductCard key={product.id} product={product} />
                        ))}
                    </div>
                </section>

                <section className="categories-section">
                    <h2 className="section-title">Shop by Category</h2>
                    <div className="category-grid">
                        <a href="/products?category=electronics" className="category-card">
                            <div className="category-icon">🖥️</div>
                            <h3>Electronics</h3>
                        </a>
                        <a href="/products?category=fashion" className="category-card">
                            <div className="category-icon">👕</div>
                            <h3>Fashion</h3>
                        </a>
                        <a href="/products?category=home" className="category-card">
                            <div className="category-icon">🏠</div>
                            <h3>Home & Living</h3>
                        </a>
                        <a href="/products?category=books" className="category-card">
                            <div className="category-icon">📚</div>
                            <h3>Books</h3>
                        </a>
                    </div>
                </section>
            </div>
        </div>
    );
};

export default Home;
